body {
    padding:100px 50px;
    text-align: center;
    font-family: 'Open Sans', sans-serif;
}

* {
    outline: none;
}

h1 {
    text-transform: uppercase;
}

.container {
    border-radius: 5px;
    padding: 30px;
    margin: 0 auto;
    text-align: center;
    width: 600px;
    padding-bottom: 50px;
    background: lightgray;
    box-shadow: 0px 0px 15px rgba(0,0,0,0.3);
}

.container_input {
    border: none;
    font-size: 20px;
    padding: 10px 20px;
    width: 50%;
    border-radius: 4px;
}

.button1 {
    width: 50%;
    background: green;
    margin-top: 20px;
    border: none;
    font-size: 20px;
    padding: 10px 20px;
    border-radius: 4px;
}

ul {
    margin-top: 30px;
    transition: all 1s ease;
}

li {
    text-align: left;
    margin-top: 2px;
    position: relative;
}

.delete {
    height: 22px;
    width: 22px;
    border: none;
    border-radius: 50%;
    background: red;
    margin-left: 10px;
    position: absolute;
    right: 25px;
    top: 9px;
}

#ul li {
    overflow: auto;
    list-style: none;
    padding: 8px 40px 10px;
    background:white;
    border-radius: 5px;
    
}
